import React, { useState } from 'react';
import axios from 'axios';

// Tuodaan shadcn/ui komponentit
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"

function App() {
  // Tilamuuttujat
  const [symbol, setSymbol] = useState('');
  const [stockData, setStockData] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Funktio osakkeen tietojen hakemiseen
  const fetchStockData = async () => {
    if (!symbol) {
      setError('Syötä osakesymboli.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await axios.get(`http://localhost:8000/api/search/?symbol=${symbol}`);
      setStockData(response.data);
    } catch (err) {
      setError('Virhe haettaessa tietoja. Tarkista symboli ja yritä uudelleen.');
      setStockData(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <h1 className="text-3xl font-bold mb-5 text-center text-gray-900">Stock Search</h1>

          <div className="mb-5 flex space-x-2">
            <Input
              type="text"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value)}
              placeholder="Syötä osakesymboli (esim. AAPL)"
              className="flex-grow"
            />
            <Button onClick={fetchStockData} disabled={loading}>
              {loading ? 'Haetaan...' : 'Hae'}
            </Button>
          </div>

          {error && (
            <p className="text-red-500 mb-5">{error}</p>
          )}

          {stockData && (
            <Card>
              <CardHeader>
                <CardTitle>{stockData.name} ({stockData.symbol})</CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-2xl font-bold">${stockData.price}</p>
                <p className={`text-sm ${stockData.change >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {stockData.change >= 0 ? '+' : ''}{stockData.change} ({stockData.changePercent}%)
                </p>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
